import React from "react";
import { Bar } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";
import "../index.css";

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

const stats = [
  { label: "Visits", value: 1200 },
  { label: "Registrations", value: 350 },
  { label: "Appointments", value: 75 },
];

const appointments = [
  { date: "2025-10-18", time: "09:00", doctor: "Dr. Smith", patient: "John Doe" },
  { date: "2025-10-18", time: "10:30", doctor: "Dr. Lee", patient: "Jane Roe" },
  { date: "2025-10-18", time: "11:15", doctor: "Dr. Brown", patient: "Alice Kim" },
];

const chartData = {
  labels: ["Visits", "Registrations", "Appointments"],
  datasets: [
    {
      label: "Count",
      data: stats.map((s) => s.value),
      backgroundColor: ["#60a5fa", "#34d399", "#fbbf24"],
    },
  ],
};

const chartOptions = {
  responsive: true,
  plugins: {
    legend: { display: false },
    title: { display: true, text: "Statistics Overview" },
  },
};

const AdminDashboard = () => {
  return (
    <div className="p-6 space-y-8">
      {/* Top Section: Stats */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {stats.map((stat) => (
          <div
            key={stat.label}
            className="bg-white shadow rounded-lg p-6 flex flex-col items-center"
          >
            <div className="text-2xl font-bold text-blue-600">{stat.value}</div>
            <div className="text-gray-500 mt-2">{stat.label}</div>
          </div>
        ))}
      </div>

      {/* Middle Section: Appointments Table */}
      <div className="bg-white shadow rounded-lg p-6">
        <h2 className="text-lg font-semibold mb-4">Today's Appointments</h2>
        <table className="min-w-full table-auto">
          <thead>
            <tr className="bg-gray-100">
              <th className="px-4 py-2 text-left">Date</th>
              <th className="px-4 py-2 text-left">Time</th>
              <th className="px-4 py-2 text-left">Doctor</th>
              <th className="px-4 py-2 text-left">Patient</th>
            </tr>
          </thead>
          <tbody>
            {appointments.map((appt, idx) => (
              <tr key={idx} className="border-b">
                <td className="px-4 py-2">{appt.date}</td>
                <td className="px-4 py-2">{appt.time}</td>
                <td className="px-4 py-2">{appt.doctor}</td>
                <td className="px-4 py-2">{appt.patient}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Bottom Section: Chart */}
      <div className="bg-white shadow rounded-lg p-6">
        <h2 className="text-lg font-semibold mb-4">Statistics Chart</h2>
        <Bar data={chartData} options={chartOptions} />
      </div>
    </div>
  );
};

export default AdminDashboard;
